pragma solidity ^0.8.8;

import {IDAO, PluginUUPSUpgradeable} from "@aragon/osx/core/plugin/PluginUUPSUpgradeable.sol";

/// @title SimpleStorage build 2
contract SimpleStorageR1B2 is PluginUUPSUpgradeable {
    bytes32 public constant STORE_PERMISSION_ID = keccak256("STORE_PERMISSION");

    uint256 public number; // added in build 1
    address public account; // added in build 2

    /// @notice Initializes the plugin when build 2 is installed.
    function initializeBuild2(
        IDAO _dao,
        uint256 _number,
        address _account
    ) external reinitializer(2) {
        __PluginUUPSUpgradeable_init(_dao);
        number = _number;
        account = _account;
    }

    /// @notice Initializes the plugin when the update from build 1 to build 2 is applied.
    /// @dev The initialization of `SimpleStorageR1B1` has already happened.
    function initializeFromBuild1(address _account) external reinitializer(2) {
        account = _account;
    }

    /// @notice Stores a new number to storage. Caller needs STORE_PERMISSION.
    /// @param _number Number to store on storage.
    function storeNumber(uint256 _number) external auth(STORE_PERMISSION_ID) {
        number = _number;
    }

    /// @notice Stores a new account to storage. Caller needs STORE_PERMISSION.
    /// @param _account Account to store on storage.
    function storeAccount(address _account) external auth(STORE_PERMISSION_ID) {
        account = _account;
    }
}
","keccak256":"0x6963eb6f42c1e436d1faddfdcc820a6fe38aba7a4b18a6bdb82138c410945ce5","license":"AGPL-3.0-or-later"},"contracts/release1/build3/SimpleStorageR1B3.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.8;

import {IDAO, PluginUUPSUpgradeable} from "@aragon/osx/core/plugin/PluginUUPSUpgradeable.sol";

/// @title SimpleStorage build 3
contract SimpleStorageR1B3 is PluginUUPSUpgradeable {
    bytes32 public constant STORE_NUMBER_PERMISSION_ID = keccak256("STORE_NUMBER_PERMISSION"); // changed in build 3
    bytes32 public constant STORE_ACCOUNT_PERMISSION_ID = keccak256("STORE_ACCOUNT_PERMISSION"); // added in build 3

    uint256 public number; // added in build 1
    address public account; // added in build 2

    // added in build 3
    event NumberStored(uint256 number);
    event AccountStored(address account);
    error AlreadyStored();

    /// @notice Initializes the plugin when build 3 is installed.
    function initializeBuild3(
        IDAO _dao,
        uint256 _number,
        address _account
    ) external reinitializer(3) {
        __PluginUUPSUpgradeable_init(_dao);
        number = _number;
        account = _account;

        emit NumberStored({number: _number});
        emit AccountStored({account: _account});
    }

    /// @notice Initializes the plugin when the update from build 2 to build 3 is applied.
    /// @dev The initialization of `SimpleStorageR1B2` has already happened.
    function initializeFromBuild2() external reinitializer(3) {
        emit NumberStored({number: number});
        emit AccountStored({account: account});
    }

    /// @notice Initializes the plugin when the update from build 1 to build 3 is applied.
    /// @dev The initialization of `SimpleStorageR1B1` has already happened.
    function initializeFromBuild1(address _account) external reinitializer(3) {
        account = _account;

        emit NumberStored({number: number});
        emit AccountStored({account: _account});
    }

    /// @notice Stores a new number to storage. Caller needs STORE_NUMBER_PERMISSION.
    /// @param _number Number to store on storage.
    function storeNumber(uint256 _number) external auth(STORE_NUMBER_PERMISSION_ID) {
        if (_number == number) revert AlreadyStored();

        number = _number;

        emit NumberStored({number: _number});
    }

    /// @notice Stores a new account to storage. Caller needs STORE_ACCOUNT_PERMISSION.
    /// @param _account Account to store on storage.
    function storeAccount(address _account) external auth(STORE_ACCOUNT_PERMISSION_ID) {
        if (_account == account) revert AlreadyStored();

        account = _account;

        emit AccountStored({account: _account});
    }
}
","keccak256":"0x6890a0064f8da47ac7e7d8cf4572c505928cb8376eb44128f4f802332e656660","license":"AGPL-3.0-or-later"},"contracts/release1/build3/SimpleStorageR1B3Setup.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-or-later

pragma solidity ^0.8.8;

import {PermissionLib} from "@aragon/osx/core/permission/PermissionLib.sol";
import {PluginSetup, IPluginSetup} from "@aragon/osx/framework/plugin/setup/PluginSetup.sol";
import {SimpleStorageR1B2} from "../build2/SimpleStorageR1B2.sol";
import {SimpleStorageR1B3} from "./SimpleStorageR1B3.sol";

/// @title SimpleStorageSetup build 3
contract SimpleStorageR1B3Setup is PluginSetup {
    address private immutable simpleStorageImplementation;

    constructor() {
        simpleStorageImplementation = address(new SimpleStorageR1B3());
    }

    /// @inheritdoc IPluginSetup
    function prepareInstallation(
        address _dao,
        bytes memory _data
    ) external returns (address plugin, PreparedSetupData memory preparedSetupData) {
        (uint256 _number, address _account) = abi.decode(_data, (uint256, address));

        plugin = createERC1967Proxy(
            simpleStorageImplementation,
            abi.encodeWithSelector(
                SimpleStorageR1B3.initializeBuild3.selector,
                _dao,
                _number,
                _account
            )
        );

        PermissionLib.MultiTargetPermission[]
            memory permissions = new PermissionLib.MultiTargetPermission[](2);

        permissions[0] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Grant,
            where: plugin,
            who: _dao,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_NUMBER_PERMISSION")
        });

        permissions[1] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Grant,
            where: plugin,
            who: _dao,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_ACCOUNT_PERMISSION")
        });

        preparedSetupData.permissions = permissions;
    }

    /// @inheritdoc IPluginSetup
    function prepareUpdate(
        address _dao,
        uint16 _currentBuild,
        SetupPayload calldata _payload
    )
        external
        pure
        override
        returns (bytes memory initData, PreparedSetupData memory preparedSetupData)
    {
        if (_currentBuild == 1) {
            address _account = abi.decode(_payload.data, (address));
            initData = abi.encodeWithSelector(
                SimpleStorageR1B3.initializeFromBuild1.selector,
                _account
            );
        } else if (_currentBuild == 2) {
            initData = abi.encodeWithSelector(SimpleStorageR1B3.initializeFromBuild2.selector);
        }

        PermissionLib.MultiTargetPermission[]
            memory permissions = new PermissionLib.MultiTargetPermission[](3);
        permissions[0] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Revoke,
            where: _dao,
            who: _payload.plugin,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_PERMISSION")
        });

        permissions[1] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Grant,
            where: _dao,
            who: _payload.plugin,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_NUMBER_PERMISSION")
        });

        permissions[2] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Grant,
            where: _dao,
            who: _payload.plugin,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_ACCOUNT_PERMISSION")
        });

        preparedSetupData.permissions = permissions;
    }

    /// @inheritdoc IPluginSetup
    function prepareUninstallation(
        address _dao,
        SetupPayload calldata _payload
    ) external pure returns (PermissionLib.MultiTargetPermission[] memory permissions) {
        permissions = new PermissionLib.MultiTargetPermission[](2);

        permissions[0] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Revoke,
            where: _payload.plugin,
            who: _dao,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_NUMBER_PERMISSION")
        });

        permissions[1] = PermissionLib.MultiTargetPermission({
            operation: PermissionLib.Operation.Revoke,
            where: _payload.plugin,
            who: _dao,
            condition: PermissionLib.NO_CONDITION,
            permissionId: keccak256("STORE_ACCOUNT_PERMISSION")
        });
    }

    /// @inheritdoc IPluginSetup
    function implementation() external view returns (address) {
        return simpleStorageImplementation;
    }
}
